    private void printSerieActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_printSerieActionPerformed
        int a = 0;
        int b = 1;
        
        DefaultListModel dlm = new DefaultListModel();
        
        lista.removeAll();
        dlm.addElement("SERIE DE FIBONACCI");
        
        for(int i = 0; i < 20; i++) {
            dlm.addElement(a);
            dlm.addElement(b);
            
            a += b;
            b += a;
        }
        
        lista.setModel(dlm);
    }//GEN-LAST:event_printSerieActionPerformed

    private void ExitLabelMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_ExitLabelMouseClicked
        System.exit(0);
    }//GEN-LAST:event_ExitLabelMouseClicked

    private void deleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_deleteActionPerformed
        DefaultListModel dlm = (DefaultListModel)lista.getModel();
        int i = lista.getSelectedIndex();
        
        dlm.remove(i);
        
        lista.setModel(dlm);
    }//GEN-LAST:event_deleteActionPerformed

