    private void clearListActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_clearListActionPerformed
        lista.removeAll();
        lista.setModel(new DefaultListModel());
    }//GEN-LAST:event_clearListActionPerformed

